import React, { useEffect, useState } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../firebase/firebase";

const Account = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);

        try {
          const response = await fetch(`/orders/${currentUser.uid}`);
          const data = await response.json();

          // Перетворення createdAt з timestamp у Date
          const sortedOrders = data.map((order) => ({
            ...order,
            createdAt: order.createdAt ? new Date(order.createdAt._seconds * 1000) : null,
          }));

          setOrders(sortedOrders);
        } catch (error) {
          console.error("Помилка при завантаженні замовлень:", error);
        }
      }
    });

    return () => unsub();
  }, []);

  return (
    <div>
      <h1>Мій акаунт</h1>
      <h2>Історія замовлень</h2>
      {!user ? (
        <p>Будь ласка, увійдіть, щоб переглянути замовлення.</p>
      ) : orders.length === 0 ? (
        <p>Замовлень ще немає.</p>
      ) : (
        orders.map((order, idx) => (
          <div key={order.id || idx} className="order">
            <h3>
              Замовлення #{idx + 1} — {order.createdAt?.toLocaleString() || "Без дати"}
            </h3>
            <ul>
              {order.items &&
                Object.entries(order.items).map(([key, item]) => (
                  <li key={key}>
                    {item.title} x {item.quantity} = {item.price * item.quantity} грн
                  </li>
                ))}
            </ul>
            <p>
              <strong>Разом:</strong>{" "}
              {order.items
                ? Object.values(order.items).reduce(
                    (acc, item) => acc + item.price * item.quantity,
                    0
                  )
                : 0}{" "}
              грн
            </p>
          </div>
        ))
      )}
    </div>
  );
};

export default Account;
