import React, { useEffect, useState } from "react";

const Cart = () => {
  const [cart, setCart] = useState({});
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("cart")) || {};
    setCart(stored);
  }, []);

  useEffect(() => {
    const totalSum = Object.values(cart).reduce(
      (sum, item) => sum + item.price * item.quantity,
      0
    );
    setTotal(totalSum);
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const updateQuantity = (title, quantity) => {
    const newCart = { ...cart };
    if (quantity <= 0) {
      delete newCart[title];
    } else {
      newCart[title].quantity = quantity;
    }
    setCart(newCart);
  };

  const checkout = () => {
    if (Object.keys(cart).length === 0) return;

    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    orders.push({ date: new Date().toLocaleString(), items: { ...cart } });
    localStorage.setItem("orders", JSON.stringify(orders));
    setCart({});
    localStorage.removeItem("cart");
  };

  return (
    <div>
      <h1>Кошик</h1>
      {Object.keys(cart).length === 0 ? (
        <p>Ваш кошик порожній.</p>
      ) : (
        <ul>
          {Object.entries(cart).map(([title, item]) => (
            <li key={title}>
              {title} x{" "}
              <input
                type="number"
                min="1"
                value={item.quantity}
                onChange={(e) => updateQuantity(title, parseInt(e.target.value))}
              />{" "}
              = {item.price * item.quantity} грн
            </li>
          ))}
        </ul>
      )}
      <p>Разом: {total} грн</p>
      <button onClick={checkout} disabled={Object.keys(cart).length === 0}>
        Оформити замовлення
      </button>
    </div>
  );
};

export default Cart;
