import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { auth, db } from "../firebase/firebase";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { onAuthStateChanged } from "firebase/auth";

const Cart = () => {
  const [cart, setCart] = useState({});
  const [total, setTotal] = useState(0);
  const [timeLeft, setTimeLeft] = useState(null);
  const [user, setUser] = useState(null);
  const timerRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsub();
  }, []);

  useEffect(() => {
    const loadCart = () => {
      const stored = JSON.parse(localStorage.getItem("cart")) || {};
      setCart(stored);
    };

    loadCart();
    window.addEventListener("focus", loadCart);
    return () => window.removeEventListener("focus", loadCart);
  }, []);

  useEffect(() => {
    const sum = Object.values(cart).reduce(
      (acc, item) => acc + item.price * item.quantity,
      0
    );
    setTotal(sum);
    if (Object.keys(cart).length > 0) {
      localStorage.setItem("cart", JSON.stringify(cart));
    }
  }, [cart]);

  const updateQuantity = (id, quantity) => {
    const updated = { ...cart };
    if (quantity <= 0) {
      delete updated[id];
    } else {
      updated[id].quantity = quantity;
      updated[id].timestamp = Date.now();
    }
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const checkout = async () => {
    if (!user) {
      alert("–ë—É–¥—å –ª–∞—Å–∫–∞, —É–≤—ñ–π–¥—ñ—Ç—å –¥–ª—è –æ—Ñ–æ—Ä–º–ª–µ–Ω–Ω—è –∑–∞–º–æ–≤–ª–µ–Ω–Ω—è.");
      navigate("/login");
      return;
    }

    if (Object.keys(cart).length === 0) return;

    try {
      await addDoc(collection(db, "orders"), {
        userId: user.uid,
        items: cart,
        createdAt: serverTimestamp(),
      });

      alert("–î—è–∫—É—î–º–æ! –í–∞—à–µ –∑–∞–º–æ–≤–ª–µ–Ω–Ω—è –æ—Ñ–æ—Ä–º–ª–µ–Ω–æ.");
      setCart({});
      localStorage.removeItem("cart");
    } catch (error) {
      alert("–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –æ—Ñ–æ—Ä–º–ª–µ–Ω–Ω—ñ –∑–∞–º–æ–≤–ª–µ–Ω–Ω—è: " + error.message);
    }
  };

  useEffect(() => {
    const syncCartAcrossTabs = (e) => {
      if (e.key === "cart") {
        const updatedCart = JSON.parse(e.newValue) || {};
        setCart(updatedCart);
      }
    };
    window.addEventListener("storage", syncCartAcrossTabs);
    return () => {
      window.removeEventListener("storage", syncCartAcrossTabs);
    };
  }, []);

  useEffect(() => {
    if (Object.keys(cart).length === 0) {
      setTimeLeft(null);
      return;
    }

    setTimeLeft(15);
    if (timerRef.current) clearInterval(timerRef.current);

    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev === 1) {
          clearInterval(timerRef.current);
          setCart({});
          localStorage.removeItem("cart");
          return null;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timerRef.current);
  }, [cart]);

  return (
    <div>
      <h1>üõí –í–∞—à –∫–æ—à–∏–∫</h1>
      {timeLeft !== null && (
        <p style={{ color: "red" }}>
          ‚è≥ –ó–∞–ª–∏—à–∏–ª–æ—Å—å —á–∞—Å—É –¥–ª—è –æ—Ñ–æ—Ä–º–ª–µ–Ω–Ω—è: {timeLeft} —Å–µ–∫.
        </p>
      )}
      {Object.keys(cart).length === 0 ? (
        <p>
          –ö–æ—à–∏–∫ –ø–æ—Ä–æ–∂–Ω—ñ–π. –ü–µ—Ä–µ–π–¥—ñ—Ç—å —É <a href="/catalog">–∫–∞—Ç–∞–ª–æ–≥</a>, —â–æ–± –¥–æ–¥–∞—Ç–∏
          –∫–Ω–∏–≥–∏.
        </p>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>–ù–∞–∑–≤–∞</th>
                <th>–ö—ñ–ª—å–∫—ñ—Å—Ç—å</th>
                <th>–¶—ñ–Ω–∞</th>
                <th>–°—É–º–∞</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(cart).map(([id, item]) => (
                <tr key={id}>
                  <td>{item.title}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) =>
                        updateQuantity(id, parseInt(e.target.value))
                      }
                    />
                  </td>
                  <td>{item.price} –≥—Ä–Ω</td>
                  <td>{item.price * item.quantity} –≥—Ä–Ω</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3>–†–∞–∑–æ–º: {total} –≥—Ä–Ω</h3>
          <button onClick={checkout}>‚úÖ –û—Ñ–æ—Ä–º–∏—Ç–∏ –∑–∞–º–æ–≤–ª–µ–Ω–Ω—è</button>
        </>
      )}
    </div>
  );
};

export default Cart;
