import React, { useState } from "react";
import booksData from "../data/books";
import BookCard from "./BookCard";

const Catalog = () => {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem("cart")) || {});
  const [filterAuthor, setFilterAuthor] = useState("all");

  const addToCart = (book) => {
    const updatedCart = { ...cart };
    if (!updatedCart[book.title]) {
      updatedCart[book.title] = { price: book.price, quantity: 1 };
    } else {
      updatedCart[book.title].quantity += 1;
    }
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const filteredBooks = booksData.filter(book =>
    filterAuthor === "all" || book.author === filterAuthor
  );

  const uniqueAuthors = [...new Set(booksData.map(b => b.author))];

  return (
    <>
      <h1>Каталог книг</h1>
      <label>Фільтрувати за автором: </label>
      <select value={filterAuthor} onChange={e => setFilterAuthor(e.target.value)}>
        <option value="all">Усі</option>
        {uniqueAuthors.map(author => (
          <option key={author} value={author}>{author}</option>
        ))}
      </select>
      <div className="book-grid">
        {filteredBooks.map(book => (
          <BookCard key={book.title} book={book} onAdd={addToCart} />
        ))}
      </div>
    </>
  );
};

export default Catalog;
