import React from "react";

const BookCard = ({ book, onAdd, cart }) => {
  const isAdded = cart[book.title];
  console.log("BookCard рендер:", book.title, "isAdded:", isAdded);

  return (
    <div className="book-card">
      <img src={book.image} alt="обкладинка книги" />
      <h3>{book.title}</h3>
      <p>Автор: {book.author}</p>
      <p>Ціна: {book.price} грн</p>
      <button
        onClick={() => {
          console.log("Клік по кнопці: додати до кошика", book);
          onAdd(book);
        }}
        className={isAdded ? "added" : ""}
      >
        {isAdded ? "Додано" : "Додати до кошика"}
      </button>
    </div>
  );
};

export default BookCard;
