import React, { useEffect, useState } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { collection, query, where, getDocs } from "firebase/firestore";
import { auth, db } from "../firebase/firebase";

const Account = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);

        const q = query(
          collection(db, "orders"),
          where("userId", "==", currentUser.uid)
        );

        const snapshot = await getDocs(q);
        const fetchedOrders = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));

        setOrders(fetchedOrders);
      }
    });

    return () => unsub();
  }, []);

  return (
    <div>
      <h1>Мій акаунт</h1>
      <h2>Історія замовлень</h2>
      {!user ? (
        <p>Будь ласка, увійдіть, щоб переглянути замовлення.</p>
      ) : orders.length === 0 ? (
        <p>Замовлень ще немає.</p>
      ) : (
        orders.map((order, idx) => (
          <div key={order.id || idx} className="order">
            <h3>
              Замовлення #{idx + 1} —{" "}
              {order.createdAt?.toDate?.().toLocaleString() || "Без дати"}
            </h3>
            <ul>
              {order.items &&
                Object.entries(order.items).map(([key, item]) => (
                  <li key={key}>
                    {item.title} x {item.quantity} ={" "}
                    {item.price * item.quantity} грн
                  </li>
                ))}
            </ul>
            <p>
              <strong>Разом:</strong>{" "}
              {order.items
                ? Object.values(order.items).reduce(
                    (acc, item) => acc + item.price * item.quantity,
                    0
                  )
                : 0}{" "}
              грн
            </p>
          </div>
        ))
      )}
    </div>
  );
};

export default Account;
