import React, { useState, useEffect } from "react";
import books from "../data/books";

const LegacyCatalog = () => {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem("cart")) || {});

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const addToCart = (book) => {
    const updatedCart = { ...cart };
    if (!updatedCart[book.title]) {
      updatedCart[book.title] = { price: book.price, quantity: 1 };
    } else {
      updatedCart[book.title].quantity += 1;
    }
    setCart(updatedCart);
  };

  const checkout = () => {
    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    orders.push(cart);
    localStorage.setItem("orders", JSON.stringify(orders));
    setCart({});
  };

  return (
    <div>
      <h1>Каталог книг</h1>
      <div className="book-grid">
        {books.map(book => (
          <div key={book.title}>
            <h2>{book.title}</h2>
            <button onClick={() => addToCart(book)}>Додати до кошика</button>
          </div>
        ))}
      </div>
      <button onClick={checkout}>Оформити замовлення</button>
    </div>
  );
};

export default LegacyCatalog;
