import React, { useEffect, useState } from "react";

const Cart = () => {
  const [cart, setCart] = useState({});
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const loadCart = () => {
      const stored = JSON.parse(localStorage.getItem("cart")) || {};
      setCart(stored);
    };

    loadCart();
    window.addEventListener("focus", loadCart);

    return () => {
      window.removeEventListener("focus", loadCart);
    };
  }, []);

  useEffect(() => {
    const sum = Object.values(cart).reduce(
      (acc, item) => acc + item.price * item.quantity,
      0
    );
    setTotal(sum);
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const updateQuantity = (id, quantity) => {
    const updated = { ...cart };
    if (quantity <= 0) {
      delete updated[id];
    } else {
      updated[id].quantity = quantity;
      updated[id].timestamp = Date.now();
    }
    setCart(updated);
    localStorage.setItem("cart", JSON.stringify(updated));
  };

  const checkout = () => {
    if (Object.keys(cart).length === 0) return;
    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    orders.push({ date: new Date().toLocaleString(), items: { ...cart } });
    localStorage.setItem("orders", JSON.stringify(orders));
    alert("Дякуємо! Ваше замовлення оформлено.");
    setCart({});
    localStorage.removeItem("cart");
  };

  useEffect(() => {
    const syncCartAcrossTabs = (e) => {
      if (e.key === "cart") {
        const updatedCart = JSON.parse(e.newValue) || {};
        setCart(updatedCart);
      }
    };
    window.addEventListener("storage", syncCartAcrossTabs);
    return () => {
      window.removeEventListener("storage", syncCartAcrossTabs);
    };
  }, []);

  return (
    <div>
      <h1>🛒 Ваш кошик</h1>
      {Object.keys(cart).length === 0 ? (
        <p>Кошик порожній. Перейдіть у <a href="/catalog">каталог</a>, щоб додати книги.</p>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>Назва</th>
                <th>Кількість</th>
                <th>Ціна</th>
                <th>Сума</th>
              </tr>
            </thead>
            <tbody>
            {Object.entries(cart).map(([id, item]) => (
            <tr key={id}>
            <td>{item.title}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => updateQuantity(id, parseInt(e.target.value))}
                    />
                  </td>
                  <td>{item.price} грн</td>
                  <td>{item.price * item.quantity} грн</td>
                </tr>
              ))}
            </tbody>
          </table>
          <h3>Разом: {total} грн</h3>
          <button onClick={checkout}>✅ Оформити замовлення</button>
        </>
      )}
    </div>
  );
};

export default Cart;
