import React from "react";

const BookCard = ({ book, onAdd, cart }) => {
  return (
    <div>
      <h2>{book.title}</h2>
      <p>{book.author}</p>
      <p>{book.price} грн</p>
      <button onClick={() => onAdd(book)} disabled={cart[book.title]}>
        {cart[book.title] ? "Додано" : "Додати до кошика"}
      </button>
    </div>
  );
};

export default BookCard;
